from fractions import Fraction

def solve():
    # States: A, B, C, D
    # Transition: from each vertex, frog moves to each adjacent vertex with prob 1/2.
    # Square: A-B-C-D-A
    # We'll compute probability distribution after 6 steps using DP.

    # Represent probabilities as Fractions for exactness
    P = {'A': Fraction(1, 1), 'B': Fraction(0, 1), 'C': Fraction(0, 1), 'D': Fraction(0, 1)}

    neighbors = {
        'A': ['B', 'D'],
        'B': ['A', 'C'],
        'C': ['B', 'D'],
        'D': ['A', 'C']
    }

    for _ in range(6):
        newP = {v: Fraction(0, 1) for v in P}
        for v in P:
            for u in neighbors[v]:
                newP[u] += P[v] * Fraction(1, 2)
        P = newP

    # Probability of being back at A after 6 jumps
    return P['A']

