def solve():
    # We solve the LP:
    # Maximize 56x + 75y
    # subject to:
    #   x + y <= 300        (land)
    #   6x + 3y <= 575      (labor)
    #   22x + 25y <= 10000  (water)
    #   x, y >= 0

    # We'll solve by checking all corner points of the feasible region.
    # Constraints in standard form: a1*x + b1*y <= c1, etc.
    import math

    # Define constraints as (a, b, c) for a*x + b*y <= c
    constraints = [
        (1.0, 1.0, 300.0),    # land
        (6.0, 3.0, 575.0),    # labor
        (22.0, 25.0, 10000.0) # water
    ]

    # Helper to check feasibility
    def feasible(x, y):
        if x < -1e-9 or y < -1e-9:
            return False
        for a, b, c in constraints:
            if a * x + b * y - c > 1e-9:
                return False
        return True

    # Helper to compute intersection of two lines: a1*x + b1*y = c1, a2*x + b2*y = c2
    def intersect(c1, c2):
        a1, b1, c1v = c1
        a2, b2, c2v = c2
        det = a1 * b2 - a2 * b1
        if abs(det) < 1e-12:
            return None
        x = (c1v * b2 - c2v * b1) / det
        y = (a1 * c2v - a2 * c1v) / det
        return (x, y)

    candidates = []

    # Intersections of each pair of constraints
    n = len(constraints)
    for i in range(n):
        for j in range(i + 1, n):
            pt = intersect(constraints[i], constraints[j])
            if pt is not None and feasible(pt[0], pt[1]):
                candidates.append(pt)

    # Intersections with axes (x=0 and y=0) for each constraint
    for a, b, c in constraints:
        # x = 0 => b*y = c
        if abs(b) > 1e-12:
            y = c / b
            x = 0.0
            if feasible(x, y):
                candidates.append((x, y))
        # y = 0 => a*x = c
        if abs(a) > 1e-12:
            x = c / a
            y = 0.0
            if feasible(x, y):
                candidates.append((x, y))

    # Also include origin if feasible
    if feasible(0.0, 0.0):
        candidates.append((0.0, 0.0))

    # Evaluate objective at all candidate points
    best_val = -math.inf
    for x, y in candidates:
        val = 56.0 * x + 75.0 * y
        if val > best_val:
            best_val = val

    return float(best_val)
